// Extract WhatsApp message data from appslink.io webhook
const webhookData = $input.first().json;

// Common fields from appslink.io
const phone = webhookData.phone || webhookData.from || webhookData.sender || '';
const message = webhookData.message || webhookData.text || webhookData.body || '';
const messageId = webhookData.messageId || webhookData.id || '';
const timestamp = webhookData.timestamp || new Date().toISOString();

// Generate transaction reference number
const refNumber = `TXN-${Date.now()}-${Math.random().toString(36).substr(2, 5).toUpperCase()}`;

return {
  phone: phone.replace(/\D/g, ''), // Clean phone number
  message: message.trim(),
  messageId,
  timestamp,
  refNumber,
  rawData: webhookData
};

// Prepare the AI response for WhatsApp
const aiResponse = $input.first().json;
const parsedMessage = $('Parse WhatsApp Message').first().json;

// Extract the AI's text response
let responseText = '';
if (typeof aiResponse.output === 'string') {
  responseText = aiResponse.output;
} else if (aiResponse.text) {
  responseText = aiResponse.text;
} else {
  responseText = JSON.stringify(aiResponse);
}

